mapper.update:
UPDATE config_version
SET version = #{version},
    content = #{content},
    status = #{status}
WHERE id = #{id}

mapper.select:
SELECT * FROM config_version
WHERE tenant_id = #{tenantId}
  AND config_type = #{configType}
  AND config_id = #{configId}
ORDER BY version DESC

SELECT * FROM config_version
WHERE tenant_id = #{tenantId}
  AND config_type = #{configType}
  AND config_id = #{configId}
  AND version = #{version}
LIMIT 1

SELECT * FROM config_version
WHERE tenant_id = #{tenantId}
  AND config_type = #{configType}
  AND config_id = #{configId}
  AND status = 'PUBLISHED'
ORDER BY version DESC
LIMIT 1

SELECT COUNT(*) FROM config_version
WHERE tenant_id = #{tenantId}
  AND config_type = #{configType}
  AND config_id = #{configId}
