mapper.select:
SELECT * FROM flow_chain
WHERE id = #{id}
  AND deleted_at IS NULL

SELECT * FROM flow_chain
WHERE tenant_id = #{tenantId}
  AND status = #{status}
  AND chain_enable = 1
  AND deleted_at IS NULL
ORDER BY created_at DESC

SELECT * FROM flow_chain
WHERE tenant_id = #{tenantId}
  AND chain_name = #{chainName}
  AND deleted_at IS NULL
LIMIT 1

SELECT COUNT(*)
FROM flow_chain
WHERE tenant_id = #{tenantId}
  AND chain_name = #{chainName}
  AND deleted_at IS NULL
