mapper.select:
SELECT * FROM rule_component
WHERE tenant_id = #{tenantId}
ORDER BY created_at DESC

SELECT * FROM rule_component
WHERE tenant_id = #{tenantId}
  AND status = #{status}
  AND script_enable = 1
ORDER BY created_at DESC

SELECT * FROM rule_component
WHERE tenant_id = #{tenantId}
  AND component_type = #{componentType}
ORDER BY created_at DESC

SELECT COUNT(*)
FROM rule_component
WHERE component_id = #{componentId}

SELECT COUNT(*)
FROM rule_component
WHERE tenant_id = #{tenantId}
