mapper.insert:
INSERT INTO saga_execution (
    execution_id, tenant_id, chain_name, status, current_step_index,
    failure_reason, input_data, output_data, execution_stack,
    started_at, completed_at, created_at, updated_at, version
) VALUES (
    #{executionId}, #{tenantId}, #{chainName}, #{status}, #{currentStepIndex},
    #{failureReason}, #{inputData}, #{outputData}, #{executionStack},
    #{startedAt}, #{completedAt}, #{createdAt}, #{updatedAt}, #{version}
)

mapper.update:
UPDATE saga_execution
SET status = #{newStatus},
    updated_at = NOW(),
    version = version + 1
WHERE execution_id = #{executionId}
  AND status = #{currentStatus}
  AND version = #{version}

mapper.select:
SELECT 
FROM saga_execution
WHERE status = #{status}
  AND completed_at < #{beforeTime}
ORDER BY completed_at
LIMIT 1000

SELECT 
FROM saga_execution
WHERE 1=1

    AND tenant_id = #{tenantId}


    AND chain_name = #{chainName}


    AND status = #{status}


    AND started_at >= #{startTime}


    AND started_at <= #{endTime}

ORDER BY started_at DESC
LIMIT #{offset}, #{limit}

SELECT 
FROM saga_execution
WHERE tenant_id = #{tenantId}

    AND status = #{status}


    AND chain_name = #{chainName}


    AND started_at >= #{startTime}


    AND started_at <= #{endTime}

ORDER BY started_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM saga_execution
WHERE tenant_id = #{tenantId}

    AND status = #{status}


    AND chain_name = #{chainName}


    AND started_at >= #{startTime}


    AND started_at <= #{endTime}
