mapper.insert:
INSERT INTO saga_step_execution (
    execution_id, step_id, component_name, status, input_data, output_data,
    compensate_component, needs_compensation, error_code, error_message,
    stack_trace, executed_at, compensated_at, created_at
) VALUES (
    #{executionId}, #{stepId}, #{componentName}, #{status}, #{inputData}, #{outputData},
    #{compensateComponent}, #{needsCompensation}, #{errorCode}, #{errorMessage},
    #{stackTrace}, #{executedAt}, #{compensatedAt}, #{createdAt}
)

mapper.update:
UPDATE saga_step_execution
SET status = #{status}
WHERE step_id = #{stepId}

UPDATE saga_step_execution
SET compensated_at = #{compensatedAt}
WHERE step_id = #{stepId}
